package java_demo;

public class variables_demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int m = 123456;
		long n = 1231243214114412L;
		float pi = 12.123456f;
		double r = 3.1234567891111111111111111111;
		char ex = 'x';
		char samp = 65;
		System.out.println("m:"+m);
		System.out.println("n:"+n);
		System.out.println("pi:"+pi);
		System.out.println("r:"+r);
		System.out.println("The value of ex:"+ex);
		ex++;
		System.out.println("THe value of ex after incrementation:"+ex);
		System.out.println("The value of samp is:"+samp);
		samp--;
		System.out.println("the value of samp after decrementing:"+samp);
		System.out.println("5>3 is :"+(5>3));
		System.out.println("100 < 10:"+(100<10));
		int demo_int = 1234567891;
		long demo_long = 1234567891234l;
		System.out.println("THe value of demo_int:"+demo_int);
		
		System.out.println("The value of demo_long:"+demo_long);
		
		
		
		
		
		
	}

}
